import React, { useEffect } from 'react';
import L from 'leaflet'; 
import 'leaflet/dist/leaflet.css'; 
import '../styles/Main.css';

function Main() {
    useEffect(() => {
        // Khởi tạo bản đồ và đặt tọa độ trung tâm ở Việt Nam
        const map = L.map('map').setView([16.4637, 107.5909], 6);

        // Sử dụng bản đồ OpenStreetMap làm nền
        L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
            attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        }).addTo(map);

        // Dữ liệu tọa độ của các trạm thu thập dữ liệu (ví dụ)
        const stations = [
            { name: 'Station 1', location: [21.0285, 105.8542] },
            { name: 'Station 2', location: [10.762622, 106.660172] },
            { name: 'Station 3', location: [16.0544, 108.2022] }
            // Thêm các trạm khác vào đây
        ];

        // Đánh dấu các điểm trạm trên bản đồ
        stations.forEach(station => {
            L.marker(station.location).addTo(map).bindPopup(`<b>${station.name}</b>`); // Thêm pop-up cho từng điểm trạm
        });
    }, []);

    return (
        <div id="map" className="map-container"></div>
    );
}

export default Main;
