import React, { useState } from 'react';
import axios from 'axios';

function Register() {
    const [formData, setFormData] = useState({
        fname: '',
        lname: '',
        email: '',
        account: '',
        password: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://sanslab.ddns.net:5000/api/user/register', formData);
            console.log('Đăng ký thành công:', response.data);
            // Thực hiện các hành động khác sau khi đăng ký thành công, ví dụ: chuyển hướng đến trang đăng nhập
        } catch (error) {
            console.error('Đăng ký không thành công:', error);
        }
    };

    return (
        <div>
            <h2>Đăng ký người dùng</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="fname">Họ:</label>
                    <input type="text" id="fname" name="fname" value={formData.fname} onChange={handleChange} />
                </div>
                <div>
                    <label htmlFor="lname">Tên:</label>
                    <input type="text" id="lname" name="lname" value={formData.lname} onChange={handleChange} />
                </div>
                <div>
                    <label htmlFor="email">Email:</label>
                    <input type="email" id="email" name="email" value={formData.email} onChange={handleChange} />
                </div>
                <div>
                    <label htmlFor="account">Tài khoản:</label>
                    <input type="text" id="account" name="account" value={formData.account} onChange={handleChange} />
                </div>
                <div>
                    <label htmlFor="password">Mật khẩu:</label>
                    <input type="password" id="password" name="password" value={formData.password} onChange={handleChange} />
                </div>
                <button type="submit">Đăng ký</button>
            </form>
        </div>
    );
}

export default Register;
