import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom'; // Import Link and useNavigate from react-router-dom
import '../styles/Login.css'; // Import stylesheet for Login component
import axios from 'axios'; // Import axios for API calls

function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate(); // Initialize useNavigate hook
  
    const handleSubmit = async (e) => {
      e.preventDefault();
      
      try {
        // Send login request to the backend
        const response = await axios.post('http://sanslab.viewdns.net:5000/api/user/login', {
            email: email,
            password: password
        });

        // Check the response from the backend
        if (response.status === 200) {
            // Save token to local storage for future requests
            localStorage.setItem('token', response.data.token);

            // Redirect the user to the main router
            navigate('/main');
        } else {
            console.error('Login failed:', response.data.error);
            // Handle login failure
        }
      } catch (error) {
        console.error('Error calling login API:', error);
        // Handle API call errors
      }
    };
  
    return (
      <div className="login-container">
        <form onSubmit={handleSubmit} className="login-form">
          <h2>Login</h2>
          <div className="form-group">
            <label htmlFor="email">Email:</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password:</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <button type="submit" className="btn-login">Login</button>
          <p className="register-link">Don't have an account? <Link to="/register">Register now</Link></p>
        </form>
      </div>
    );
}

export default Login;
