import React, { useEffect, useRef } from 'react';
import L from 'leaflet'; 
import 'leaflet/dist/leaflet.css'; 
import '../styles/Main.css';
import iconUrl from 'https://img.lovepik.com/free-png/20210927/lovepik-coordinate-png-image_401562875_wh1200.png'; // Đường dẫn đến icon

function Main() {
    const mapContainer = useRef(null);

    useEffect(() => {
        if (!mapContainer.current || mapContainer.current.children.length === 0) {
            // Khởi tạo bản đồ và đặt tọa độ trung tâm ở Việt Nam
            const map = L.map(mapContainer.current).setView([16.4637, 107.5909], 6);

            // Sử dụng bản đồ OpenStreetMap làm nền
            L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
                attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            }).addTo(map);

            // Tạo icon tùy chỉnh
            const customIcon = L.icon({
                iconUrl: iconUrl, // Đường dẫn đến icon
                iconSize: [25, 41], // Kích thước của icon
                iconAnchor: [12, 41], // Vị trí chính xác của icon, thường là phía dưới cùng giữa
                popupAnchor: [0, -41] // Vị trí hiển thị pop-up, thường là phía trên cùng giữa
            });

            // Dữ liệu tọa độ của các trạm thu thập dữ liệu (ví dụ)
            const stations = [
                { name: 'Station 1', location: [21.0285, 105.8542] },
                { name: 'Station 2', location: [10.762622, 106.660172] },
                { name: 'Station 3', location: [16.0544, 108.2022] }
                // Thêm các trạm khác vào đây
            ];

            // Đánh dấu các điểm trạm trên bản đồ với icon tùy chỉnh
            stations.forEach(station => {
                L.marker(station.location, { icon: customIcon }).addTo(map).bindPopup(`<b>${station.name}</b>`); // Thêm pop-up cho từng điểm trạm
            });
        }
    }, [mapContainer]);

    return <div ref={mapContainer} id="map" className="map-container"></div>;
}

export default Main;
