import React, { useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import '../styles/Login.css';
import axios from 'axios'; // Import axios for API calls

function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const history = useHistory(); // Initialize useHistory hook

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        try {
            // Gửi yêu cầu đăng nhập tới backend
            const response = await axios.post('http://sanslab.viewdns.net:5000/api/user/login', {
                email: email,
                password: password
            });

            // Kiểm tra phản hồi từ backend
            if (response.status === 200) {
                // Lưu token vào local storage để sử dụng cho các yêu cầu sau này
                localStorage.setItem('token', response.data.token);

                // Chuyển hướng người dùng vào router chính
                history.push('/main');
            } else {
                console.error('Đăng nhập không thành công:', response.data.error);
                // Xử lý lỗi đăng nhập không thành công
            }
        } catch (error) {
            console.error('Lỗi khi gọi API đăng nhập:', error);
            // Xử lý lỗi khi gọi API
        }
    };

    return (
        <div className="login-container">
            <form onSubmit={handleSubmit} className="login-form">
                <h2>Đăng nhập</h2>
                <div className="form-group">
                    <label htmlFor="email">Email:</label>
                    <input
                        type="email"
                        id="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="password">Mật khẩu:</label>
                    <input
                        type="password"
                        id="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                </div>
                <button type="submit" className="btn-login">Đăng nhập</button>
                <p className="register-link">Chưa có tài khoản? <Link to="/register">Đăng ký ngay</Link></p>
            </form>
        </div>
    );
}

export default Login;
